query.script:
importPackage(org.wandora.query2);
importPackage(org.wandora.topicmap);
new Union(
  new Types().as(XTMPSI.CLASS)
  .join(new Identity().as(XTMPSI.INSTANCE)),
  new Instances().as(XTMPSI.INSTANCE)
  .join(new Identity().as(XTMPSI.CLASS))
);

importPackage(org.wandora.query2);
importPackage(org.wandora.topicmap);
new Roles(XTMPSI.SUPERCLASS,XTMPSI.SUBCLASS).from(
  new Unique(new Union(
    new Recursive(
      new Players(XTMPSI.SUPERCLASS_SUBCLASS,XTMPSI.SUPERCLASS)
      .whereInputIs(XTMPSI.SUBCLASS)
    ).as(XTMPSI.SUPERCLASS).join(new Identity().as(XTMPSI.SUBCLASS)),
    new Recursive(
      new Players(XTMPSI.SUPERCLASS_SUBCLASS,XTMPSI.SUBCLASS)
      .whereInputIs(XTMPSI.SUPERCLASS)
    ).as(XTMPSI.SUBCLASS).join(new Identity().as(XTMPSI.SUPERCLASS))
  ))
);

processingTopicPanel.currentSketch:
/* 
 * Welcome to Wandora's Processing topic panel!
 * 
 * Processing topic panel is used to write and run Processing scripts.
 * Processing script can access the topic map in Wandora via Wandora's Java API.
 * Topic panel suits best for topic and topic map visualizations.
 * Writing a Processing script requires programming skills.
 * 
 * You might want to begin by trying one of our demo sketches. We have
 * prepared three example scripts to inspire you. These sketces, titled as
 * AssociationArches, TopicBoxes and TopicRing can be opened by clicking
 * the Open button and selecting option Open sketch from file...
 * To run a script press Run button down under.
 * 
 * Learn Processing and Wandora API here
 *    http://processing.org/
 *    http://wandora.org/api/ 
 */

package processing;

import org.wandora.application.gui.topicpanels.processing.SketchTemplate;
import org.wandora.topicmap.*;

public class Sketch extends SketchTemplate {

	public void setup() {
		size(920,640);
		frameRate(10);
	}

	public void draw() {
		background(255);
		Topic t = getCurrentTopic();
		if( t != null ) {
			try{
				fill(0xff000000);
				text(t.getBaseName(),100,100);
			} catch(TopicMapException tme){tme.printStackTrace();}
		}
	}
}

connection.script:
create cached table TOPIC(
TOPICID varchar(255) primary key,
BASENAME longvarchar,
SUBJECTLOCATOR longvarchar 
);

create cached table TOPICTYPE(
TOPIC varchar(255) not null,
TYPE varchar(255) not null,
foreign key (TOPIC) references TOPIC(TOPICID),
foreign key (TYPE) references TOPIC(TOPICID),
primary key(TOPIC,TYPE)
);

create cached table DATA(
TOPIC varchar(255) not null,
TYPE varchar(255) not null,
VERSION varchar(255) not null,
DATA longvarchar,
foreign key (TOPIC) references TOPIC(TOPICID),
foreign key (TYPE) references TOPIC(TOPICID),
foreign key (VERSION) references TOPIC(TOPICID),
primary key(TOPIC,TYPE,VERSION)
);

create cached table VARIANT(
VARIANTID varchar(255) primary key,
TOPIC varchar(255) not null,
VALUE longvarchar,
foreign key (TOPIC) references TOPIC(TOPICID)
);

create cached table VARIANTSCOPE(
VARIANT varchar(255) not null,
TOPIC varchar(255) not null,
foreign key (VARIANT) references VARIANT(VARIANTID),
foreign key (TOPIC) references TOPIC(TOPICID),
primary key(VARIANT,TOPIC)
);

create cached table SUBJECTIDENTIFIER(
SI longvarchar primary key,
TOPIC varchar(255) not null,
foreign key (TOPIC) references TOPIC(TOPICID)
);

create cached table ASSOCIATION(
ASSOCIATIONID varchar(255) primary key,
TYPE varchar(255) not null,
foreign key (TYPE) references TOPIC(TOPICID)
);

create cached table MEMBER(
ASSOCIATION varchar(255) not null,
PLAYER varchar(255) not null,
ROLE varchar(255) not null,
foreign key (ASSOCIATION) references ASSOCIATION(ASSOCIATIONID),
foreign key (PLAYER) references TOPIC(TOPICID),
foreign key (ROLE) references TOPIC(TOPICID),
primary key(ASSOCIATION,ROLE)
);

create index BASENAME_INDEX ON TOPIC(BASENAME);
create index SUBJECTLOCATOR_INDEX ON TOPIC(SUBJECTLOCATOR);
create index SITOPIC_INDEX on SUBJECTIDENTIFIER(TOPIC);
create index ASSOCTYPE_INDEX on ASSOCIATION(TYPE);
create index MEMBERPLAYER_INDEX on MEMBER(PLAYER);
create index MEMBERROLE_INDEX on MEMBER(ROLE);
create index VARIANTTOPIC_INDEX on VARIANT(TOPIC);
create index DATATOPIC_INDEX on DATA(TOPIC);
create index DATATYPE_INDEX on DATA(TYPE);
create index DATAVERSION_INDEX on DATA(VERSION);

query.script:
importPackage(org.wandora.query2);
importPackage(org.wandora.topicmap);
new Unique(
  new Roles("#instance").from(
    new Union(
      new Instances().as("#instance").from(
        new Recursive(
          new Players(XTMPSI.SUPERCLASS_SUBCLASS,XTMPSI.SUBCLASS)
          .whereInputIs(XTMPSI.SUPERCLASS)
        )
      ),
      new Instances().as("#instance")
    )
  )
);

importPackage(org.wandora.query2);
importPackage(org.wandora.topicmap);
new Count(
  new Union(
    new Types(),
    new Players(XTMPSI.SUPERCLASS_SUBCLASS,XTMPSI.SUPERCLASS)
      .whereInputIs(XTMPSI.SUBCLASS)
  )
).as("#count").from(new AllTopics().as("#topic"))
.where(new Of("#count"),"=","0");

importPackage(org.wandora.query2);
new Count(
   new Instances().from( new AllTopics() )
)
.as("#Total instance count");

importPackage(org.wandora.query2);
new BaseName().from(
   new AllTopics()
)
.where(
   new Regex(".{4}")
).as("#Topics with four letter base names")

query.query:
INSERT '''
%prefix core http://wandora.org/si/core/
%prefix test http://wandora.org/si/test/

testctype - "Test content type" ;
  test:testctype ;
  isa core:content-type .

testatype - "Test association type" ;
  test:testatype ;
  isa core:association-type .

testrole1 - "Test role 1" ;
  test:testrole1 ;
  isa core:role .

testrole2 - "Test role 2" ;
  test:testrole2 ;
  isa core:role .

core:association-type( core:content-type : test:testctype , core:association-type : test:testatype )
core:role( core:association-type : test:testatype , core:role : test:testrole1 )
core:role( core:association-type : test:testatype , core:role : test:testrole2 )
'''
